sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below

	var what_type = g_form.getValue('u_what_type');
	jslog('Value of "What Type": '+what_type);
	jslog('Value of "newValue": '+newValue);

		g_form.clearOptions('u_what_type');

		if(newValue == 'hr') {
			jslog('Adding options to "What type" related to Human Resource');
			g_form.addOption('u_what_type','hr1','Human Resource 1');
			g_form.addOption('u_what_type','hr3','Human Resource 3');
		}

		 if(newValue == 'legal') {
			 jslog('Adding options to "What type" related to Legal');
			g_form.addOption('u_what_type','legal1','Legal 1');
			g_form.addOption('u_what_type','legal2','Legal 2');
			g_form.addOption('u_what_type','legal3','Legal 3');
		}

		 if(newValue == 'facilities') {
			 jslog('Adding options to "What type" related to Facilities');
			g_form.addOption('u_what_type','facilities1','Facilities 1');
			g_form.addOption('u_what_type','facilities2','Facilities 2');
		}
		else {
			g_form.addOption('u_what_type','other','Other');
		}

	if(isLoading && !g_form.isNewRecord()) {
			g_form.setValue('u_what_type',what_type);

		}

}
