sys_script_include.script:
var GetApprovers = Class.create();
GetApprovers.prototype = {
    initialize: function() {
    },
	getApprovers:function(){
		gs.addInfoMessage('called');
		var gp = new GlideRecord('u_approvers');
		var catId = current.variables.u_catalog_item_id;
		gs.addInfoMessage(catId);

		gp.addQuery('u_catalog_item',catId);
		gp.addQuery('u_approver_type','A');
		gp.addQuery('u_active',1);
		gp.query();

		while(gp.next()){
			g_form.addOption('u_a_approver',gp.u_user,gp.u_user_name);
		}

	},
    type: 'GetApprovers'
};
